DEFINE TENANT_ID ='&1'
SET FEEDBACK ON
SET SERVEROUTPUT ON


---add 3 OOB TA OOB widgets
DECLARE
  v_count number;
  v_searchid number:=3029;
  v_searchid_2 number:=3030;
  v_searchid_3 number:=3031;
BEGIN

    SELECT COUNT(1) INTO v_count FROM EMS_ANALYTICS_SEARCH WHERE search_id=v_searchid AND TENANT_ID  ='&TENANT_ID';
    IF (v_count=0) THEN
        values (v_searchid,'OMC Oracle Database - Transactions Rate','ORACLE',SYS_EXTRACT_UTC(SYSTIMESTAMP),SYS_EXTRACT_UTC(SYSTIMESTAMP),'ORACLE','OMC Oracle Database - Avg Transactions Rate by Hour',4,2,null,null,null,null,1,null,0,null,null,0,'&TENANT_ID',1);





        

        DBMS_OUTPUT.PUT_LINE('OOB widget (id='||v_searchid||') is added');  
    ELSE
      DBMS_OUTPUT.PUT_LINE('OOB widget (id='||v_searchid||') exists, no need to add again');  
    END IF;  
    
    SELECT COUNT(1) INTO v_count FROM EMS_ANALYTICS_SEARCH WHERE search_id=v_searchid_2 AND TENANT_ID  ='&TENANT_ID';
    IF (v_count=0) THEN
        values (v_searchid_2,'OMC WebLogic Server - Web Request Rate','ORACLE',SYS_EXTRACT_UTC(SYSTIMESTAMP),SYS_EXTRACT_UTC(SYSTIMESTAMP),'ORACLE','OMC WebLogic Server - Avg Web Request Rate by Hour',4,2,null,null,null,null,1,null,0,null,null,0,'&TENANT_ID',1);







        DBMS_OUTPUT.PUT_LINE('OOB widget (id='||v_searchid_2||') is added');  
    ELSE
      DBMS_OUTPUT.PUT_LINE('OOB widget (id='||v_searchid_2||') exists, no need to add again');  
    END IF;  
    
    SELECT COUNT(1) INTO v_count FROM EMS_ANALYTICS_SEARCH WHERE search_id=v_searchid_3 AND TENANT_ID  ='&TENANT_ID';
    IF (v_count=0) THEN
        values (v_searchid_3,'OMC Host CPU Utilization','ORACLE',SYS_EXTRACT_UTC(SYSTIMESTAMP),SYS_EXTRACT_UTC(SYSTIMESTAMP),'ORACLE','OMC Host - Avg CPU Utilization by Hour',4,2,null,null,null,null,1,null,0,null,null,0,'&TENANT_ID',1);





      

        DBMS_OUTPUT.PUT_LINE('OOB widget (id='||v_searchid_3||') is added');  
    ELSE
      DBMS_OUTPUT.PUT_LINE('OOB widget (id='||v_searchid_3||') exists, no need to add again');  
    END IF;  
    
    COMMIT;

EXCEPTION
WHEN OTHERS THEN
    ROLLBACK;
    DBMS_OUTPUT.PUT_LINE('Failed to add 3 TA OOB WIDGET due to '||SQLERRM);   
    RAISE;  
    
END;
/
